const submitForm=document.querySelector(".todo-list__form"),input=document.querySelector(".todo-list__input"),addButton=document.querySelector(".icon-plus"),list=document.querySelector(".todo-list__list"),listItems=document.querySelectorAll(".todo-list__item");let listLength=listItems.length;function generateItem(t){const e=`\n    <li class="todo-list__item">\n    <input class="todo-list__checkbox" type="checkbox" id="item_${listLength}" />\n    <label class="todo-list__item-text" for="item_${listLength}">\n      ${t}\n    </label>\n    <span class="todo-list__button icon-trash"></span>\n  </li>`;list.innerHTML+=e}function addItem(t){t.preventDefault();const e=input.value.trim();e.length&&(listLength+=1,generateItem(e),submitForm.reset())}function deleteItem(t){t.target.classList.contains("icon-trash")&&t.target.parentElement.remove()}submitForm.addEventListener("submit",addItem),addButton.addEventListener("click",addItem),list.addEventListener("click",deleteItem);